// Helper function to get today's readings
function getTodaysReadings() {
  const now = new Date();
  const month = now.getMonth() + 1; // JavaScript months are 0-indexed
  const day = now.getDate();

  if (lectionary[month] && lectionary[month][day]) {
    return lectionary[month][day];
  }
  return null;
}

// Helper function to parse a Bible reference
function parseReference(ref) {
  // Handle references like "Genesis 1:1-20", "Acts 1", "Matthew 1:18-25"
  const match = ref.match(/^(.+?)\s+(\d+)(?::(\d+)(?:-(\d+))?)?$/);
  if (match) {
    return {
      book: match[1],
      chapter: parseInt(match[2]),
      verseStart: match[3] ? parseInt(match[3]) : 1,
      verseEnd: match[4] ? parseInt(match[4]) : null
    };
  }
  return null;
}

// Format introduction for a lesson
function formatLessonIntro(ref, lessonNumber) {
  const parsed = parseReference(ref);
  if (!parsed) return `The ${lessonNumber} Lesson`;

  const ordinal = lessonNumber === 'first' ? 'First' : 'Second';

  if (parsed.verseStart === 1 && !parsed.verseEnd) {
    // Full chapter
    return `The ${ordinal} Lesson is written in the ${parsed.chapter}${getOrdinalSuffix(parsed.chapter)} chapter of ${parsed.book}.`;
  } else {
    return `The ${ordinal} Lesson is written in the ${parsed.chapter}${getOrdinalSuffix(parsed.chapter)} chapter of ${parsed.book}, beginning at the ${parsed.verseStart}${getOrdinalSuffix(parsed.verseStart)} verse.`;
  }
}

// Get ordinal suffix (1st, 2nd, 3rd, etc.)
function getOrdinalSuffix(n) {
  const s = ['th', 'st', 'nd', 'rd'];
  const v = n % 100;
  return (s[(v - 20) % 10] || s[v] || s[0]);
}
